
public class Looping_For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, j;
		for(i = 1; i < 6; i++)
			{
			for(j = 1; j <= i; j++)
			{
				System.out.print(+j);
			}
			//System.out.println();
			System.out.print("\n");
			}
			
		}
	

}
